<script>
    //Global Helper Functions

    function removeElByID(id) {
        var elem = document.getElementById(id);
        if (elem) {
            elem.parentElement.removeChild(elem);
        }
    }

    var $documentHead = document.getElementsByTagName('head')[0];

    function addScriptToHead(url, id) {
        var script = document.createElement('script');
        script.type = 'text/javascript';
        script.src = url;
        script.id = id;
        $documentHead.appendChild(script);
    }

    CPABuildCheckForLead();

    function CPABuildCheckForLead() {
        //CPABuildCheckForLead is called by check.php

        checkLeadScript();
    }

    function CPABuildOffersComplete(arr) {
        //CPABuildOffersComplete is called by check.php when leads completed
        return templateHandleLeadsComplete(arr);
    }

    function checkLeadScript() {
        removeElByID("CPABUILDLEADCHECK");
        addScriptToHead("//app.cpbldi.com/public/external/check.php?time=" + new Date().getTime() + "&it=" + $GET.it, "CPABUILDLEADCHECK");
    }
</script>
<script type="text/javascript">
    //Impression Logging

    var logged_impression = false;
    var cpaBuildImpressionLogger = function () {
        document.removeEventListener('mousemove', cpaBuildImpressionLogger, false);
        window.removeEventListener('mousemove', cpaBuildImpressionLogger, false);
        document.removeEventListener('click', cpaBuildImpressionLogger, false);
        window.removeEventListener('click', cpaBuildImpressionLogger, false);
        document.removeEventListener('touchstart', cpaBuildImpressionLogger, false);
        window.removeEventListener('touchstart', cpaBuildImpressionLogger, false);

        if (logged_impression === false) {
            logged_impression = true;
            addScriptToHead("/public/external/impression.php?time=" + new Date().getTime(), "CPABUILDIMPRESSIONLOG");
        }
    };
    document.addEventListener('mousemove', cpaBuildImpressionLogger, false);
    window.addEventListener('mousemove', cpaBuildImpressionLogger, false);
    document.addEventListener('click', cpaBuildImpressionLogger, false);
    window.addEventListener('click', cpaBuildImpressionLogger, false);
    document.addEventListener('touchstart', cpaBuildImpressionLogger, false);
    window.addEventListener('touchstart', cpaBuildImpressionLogger, false);
    if ($GET.iframe_mode == 1) cpaBuildImpressionLogger(); //Automatically log if iframe

</script>
