    }
    var cssRules=[
        {target:"#continueButton",rule:"border",value:"1px solid "+userSettings.primary_color},
        {target:"#continueButton",rule:"background",value:""+userSettings.primary_color},
        {target:".veriCaptchaLogo .fa",rule:"color",value:""+userSettings.primary_color},
    ];
    var offerContainer=$('#offerContainer');
    $('#continueButton').text(getSettingsKey("verification_captcha_button"));
    offerContainer.find('.offerBoxTitle').text(userSettings.offer_box_title);
    offerContainer.find('.offerBoxSubTitle').text(userSettings.offer_box_sub_title);
    setCSSRules(cssRules);
</script>
<script type="application/javascript">
    var CPABuildLeads={};
    var offersNeeded=parseInt(parseFloat(userSettings.number_offers_required));
    if(offersNeeded < 1)offersNeeded=1;
    var offersCompleted=0;

    var payoutNeeded=parseInt(parseFloat(userSettings.payout_required));
    if(payoutNeeded < 1)payoutNeeded=1;
    var payoutCompleted=0;


    function templateHandleLeadsComplete(arr){
        $.each(arr,function(index,obj){
            CPABuildLeads[obj.lead_id]=obj;
        });
        payoutCompleted=0;
        offersCompleted=0;
        $.each(CPABuildLeads,function(lead_id,obj){
            offersCompleted+=1;
            payoutCompleted+=parseInt(obj.points);
        });
        if(offersCompleted < offersNeeded){
            $.each(CPABuildLeads,function(lead_id,obj){
                var els=$('a.offerURL[data-offerid="'+obj.offer_id+'"]');
                els.text("[OFFER COMPLETED - COMPLETE "+(offersNeeded-offersCompleted)+" MORE OFFER(S)]").attr("href","#").off("click").css("color","green");
            });
        }
        else if(payoutCompleted < payoutNeeded){
            $.each(CPABuildLeads,function(lead_id,obj){
                var els=$('a.offerURL[data-offerid="'+obj.offer_id+'"]');
                els.text("[OFFER COMPLETED FOR "+(obj.points)+" POINTS - NEED "+(payoutNeeded-payoutCompleted)+" MORE POINT(S)]").attr("href","#").off("click").css("color","green");
            });
        }
        else{
            //Complete!
            if(userSettings.onClose=="redirect"){
                var $a=$("<a>OFFERS COMPLETE! CLICK HERE TO CONTINUE</a>");
                $a.attr("href",userSettings.onCloseURL);
                $('body').html("");
                $a.appendTo($('body'));
            }
        }

    }
</script>
</body>
